import React, { useState } from "react";
import useInterval from "../utils/useInterval";
import Session from "./Session";
import IntervalButtons from "./IntervalButtons";
import TimerControls from "./TimerControls";

function nextTick(prevState) {
  const timeRemaining = Math.max(0, prevState.timeRemaining - 1);
  return {
    ...prevState,
    timeRemaining,
  };
}


/* State function to transition the current session type to the 
next session. e.g. On Break -> Focusing or Focusing -> On Break */

function nextSession(focusDuration, breakDuration) {

  return (currentSession) => {
    if (currentSession.label === "Focusing") {
      return {
        label: "On Break",
        timeRemaining: breakDuration * 60,
      };
    }
    return {
      label: "Focusing",
      timeRemaining: focusDuration * 60,
    };
  };
}

function Pomodoro() {

  //Timer starts paused
  const [isTimerRunning, setIsTimerRunning] = useState(false);

  //Current session is null
  const [session, setSession] = useState(null);


  const [focusDuration, setFocusDuration] = useState(25);
  const [breakDuration, setBreakDuration] = useState(5);


  // This section handles the decrements and increments of the buttons
  const focusDecreaseHandler = () => {
    if (focusDuration > 5) setFocusDuration(focusDuration - 5);
  };

  const focusIncreaseHandler = () => {
    if (focusDuration < 60) setFocusDuration(focusDuration + 5);
  };

  const breakDecreaseHandler = () => {
    if (breakDuration > 1) {
      setBreakDuration(breakDuration - 1);
    }
  };

  const breakIncreaseHandler = () => {
    if (breakDuration < 15) {
      setBreakDuration(breakDuration + 1);
    }
  };

  //Stop button 
  const handleStopSession = () => {
    setIsTimerRunning(false);
    setSession(null);
  };

  const displayDuration = (label) => {
    if (label === "Focusing") {
      return focusDuration;
    } else return breakDuration;
  };

  const updateAria = (time, label) => {
    return 100 - (time / (displayDuration(label) * 60)) * 100;
  };

  const updatedAria = updateAria(session?.timeRemaining, session?.label);

  useInterval(
    () => {
      if (session.timeRemaining === 0) {
        new Audio(`https://bigsoundbank.com/UPLOAD/mp3/1482.mp3`).play();
        return setSession(nextSession(focusDuration, breakDuration));
      }
      return setSession(nextTick);
    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => {
      const nextState = !prevState;
      if (nextState) {
        setSession((prevStateSession) => {
          if (prevStateSession === null) {
            return {
              label: "Focusing",
              timeRemaining: focusDuration * 60,
            };
          }
          return prevStateSession;
        });
      }
      return nextState;
    });
  }

  return (
    <div className="pomodoro">
      <IntervalButtons
        session={session}
        focusDuration={focusDuration}
        breakDuration={breakDuration}
        focusDecreaseHandler={focusDecreaseHandler}
        focusIncreaseHandler={focusIncreaseHandler}
        breakDecreaseHandler={breakDecreaseHandler}
        breakIncreaseHandler={breakIncreaseHandler}
      />
      <TimerControls
        playPause={playPause}
        isTimerRunning={isTimerRunning}
        handleStopSession={handleStopSession}
        session={session}
      />
      <Session
        session={session}
        updatedAria={updatedAria}
        displayDuration={displayDuration}
        isTimerRunning={isTimerRunning}
      />
    </div>
  );
}

export default Pomodoro;
